
public class BreakContinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		for (int i=0; i<10;i++) {
			
			if(i>=5 && i<=7)
			{
				continue;
			}// continue will skip the iterations for which the if condition is satisfied., here it omits 5,6&7;
			
			
			System.out.println(i);

			
			
		}
		
		
		
	}

}
